package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.DelegationModel;
import org.ebs.services.to.Input.DelegationInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:13:31 PM
 */
@Component
  class DelegationConverterInput implements Converter<DelegationInput,DelegationModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public DelegationModel convert(DelegationInput source){
		DelegationModel target = new  DelegationModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}