package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.DomainModel;
import org.ebs.services.to.DomainTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:19:46 AM
 */
@Component
  class DomainConverter implements Converter<DomainModel,DomainTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public DomainTo convert(DomainModel source){
		DomainTo target = new  DomainTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}