package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.DomainInstanceModel;
import org.ebs.services.to.DomainInstanceTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:19:46 AM
 */
@Component
  class DomainInstanceConverter implements Converter<DomainInstanceModel,DomainInstanceTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public DomainInstanceTo convert(DomainInstanceModel source){
		DomainInstanceTo target = new  DomainInstanceTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}