package org.ebs.services.converter;

import org.springframework.beans.BeanUtils;
import org.ebs.model.DonorModel;
import org.ebs.services.to.Input.DonorInput;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 09-Aug-2020 4:50:36 PM
 */
@Component
  class DonorConverterInput implements Converter<DonorInput,DonorModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public DonorModel convert(DonorInput source){
		DonorModel target = new  DonorModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}