package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.EventModel;
import org.ebs.services.to.EventTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:29 AM
 */
@Component
  class EventConverter implements Converter<EventModel,EventTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public EventTo convert(EventModel source){
		EventTo target = new  EventTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}