package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.FunctionalUnitModel;
import org.ebs.services.to.FunctionalUnitTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:19:49 AM
 */
@Component
  class FunctionalUnitConverter implements Converter<FunctionalUnitModel,FunctionalUnitTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public FunctionalUnitTo convert(FunctionalUnitModel source){
		FunctionalUnitTo target = new  FunctionalUnitTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}