package org.ebs.services.converter;

import org.ebs.model.LanguageModel;
import org.ebs.services.to.Input.LanguageInput;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:38 PM
 */
@Component
public class LanguageConverterInput implements Converter<LanguageInput,LanguageModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public LanguageModel convert(LanguageInput source){
		LanguageModel target = new  LanguageModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}