package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.MessageModel;
import org.ebs.services.to.Input.MessageInput;
import org.ebs.services.to.MessageTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:42 PM
 */
@Component
public class MessageConverter implements Converter<MessageModel,MessageTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public MessageTo convert(MessageModel source){
		MessageTo target = new  MessageTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}