package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.MessageModel;
import org.ebs.services.to.Input.MessageInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 07-Aug-2020 3:00:00 PM
 */
@Component
  class MessageConverterInput implements Converter<MessageInput,MessageModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public MessageModel convert(MessageInput source){
		MessageModel target = new  MessageModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}