package org.ebs.services.converter;

import org.ebs.model.ModuleModel;
import org.ebs.services.to.ModuleTo;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:43 PM
 */
@Component
public class ModuleConverter implements Converter<ModuleModel,ModuleTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ModuleTo convert(ModuleModel source){
		ModuleTo target = new  ModuleTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}