package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.ModuleModel;
import org.ebs.services.to.Input.ModuleInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Jul-2020 8:42:46 AM
 */
@Component
  class ModuleConverterInput implements Converter<ModuleInput,ModuleModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ModuleModel convert(ModuleInput source){
		ModuleModel target = new  ModuleModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}