package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.NodeCfModel;
import org.ebs.services.to.NodeCfTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:36 AM
 */
@Component
  class NodeCfConverter implements Converter<NodeCfModel,NodeCfTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public NodeCfTo convert(NodeCfModel source){
		NodeCfTo target = new  NodeCfTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}