package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.NodeModel;
import org.ebs.services.to.Input.NodeInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:38 AM
 */
@Component
  class NodeConverterInput implements Converter<NodeInput,NodeModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public NodeModel convert(NodeInput source){
		NodeModel target = new  NodeModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}