package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.NotificationModel;
import org.ebs.services.to.NotificationTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 07-Sep-2020 8:11:32 AM
 */
@Component
  class NotificationConverter implements Converter<NotificationModel,NotificationTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public NotificationTo convert(NotificationModel source){
		NotificationTo target = new  NotificationTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}