package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.NotificationModel;
import org.ebs.services.to.Input.NotificationInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 04-Sep-2020 10:05:24 AM
 */
@Component
  class NotificationConverterInput implements Converter<NotificationInput,NotificationModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public NotificationModel convert(NotificationInput source){
		NotificationModel target = new  NotificationModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}