package org.ebs.services.converter;

import org.springframework.beans.BeanUtils;
import org.ebs.model.OrganizationModel;
import org.ebs.services.to.OrganizationTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Jul-2020 8:42:56 AM
 */
@Component
  class OrganizationConverter implements Converter<OrganizationModel,OrganizationTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public OrganizationTo convert(OrganizationModel source){
		OrganizationTo target = new  OrganizationTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}