package org.ebs.services.converter;

import org.springframework.beans.BeanUtils;
import org.ebs.model.OrganizationModel;
import org.ebs.services.to.Input.OrganizationInput;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 09-Aug-2020 4:51:01 PM
 */
@Component
  class OrganizationConverterInput implements Converter<OrganizationInput,OrganizationModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public OrganizationModel convert(OrganizationInput source){
		OrganizationModel target = new  OrganizationModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}