package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.PearsonModel;
import org.ebs.services.to.Input.PearsonInput;
import org.ebs.services.to.PearsonTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Apr-2020 9:47:47 AM
 */
@Component
public class PearsonConverter implements Converter<PearsonModel,PearsonTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public PearsonTo convert(PearsonModel source){
		PearsonTo target = new  PearsonTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}