package org.ebs.services.converter;

import org.springframework.beans.BeanUtils;
import org.ebs.model.PermissionModel;
import org.ebs.services.to.PermissionTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:13:54 PM
 */
@Component
  class PermissionConverter implements Converter<PermissionModel,PermissionTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public PermissionTo convert(PermissionModel source){
		PermissionTo target = new  PermissionTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}