package org.ebs.services.converter;

import org.ebs.model.PersonModel;
import org.ebs.services.to.PersonTo;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:52 PM
 */
@Component
public class PersonConverter implements Converter<PersonModel,PersonTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public PersonTo convert(PersonModel source){
		PersonTo target = new  PersonTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}