package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.PersonStatusModel;
import org.ebs.services.to.PersonStatusTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:19:55 AM
 */
@Component
  class PersonStatusConverter implements Converter<PersonStatusModel,PersonStatusTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public PersonStatusTo convert(PersonStatusModel source){
		PersonStatusTo target = new  PersonStatusTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}