package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.PhaseModel;
import org.ebs.services.to.PhaseTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:46 AM
 */
@Component
  class PhaseConverter implements Converter<PhaseModel,PhaseTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public PhaseTo convert(PhaseModel source){
		PhaseTo target = new  PhaseTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}