package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.ProductFunctionModel;
import org.ebs.services.to.ProductFunctionTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:19:57 AM
 */
@Component
  class ProductFunctionConverter implements Converter<ProductFunctionModel,ProductFunctionTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ProductFunctionTo convert(ProductFunctionModel source){
		ProductFunctionTo target = new  ProductFunctionTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}