package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.ProductFunctionModel;
import org.ebs.services.to.Input.ProductFunctionInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:19:57 AM
 */
@Component
  class ProductFunctionConverterInput implements Converter<ProductFunctionInput,ProductFunctionModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ProductFunctionModel convert(ProductFunctionInput source){
		ProductFunctionModel target = new  ProductFunctionModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}