package org.ebs.services.converter;

import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.ProgramModel;
import org.ebs.services.to.Input.ProgramInput;
import org.ebs.services.to.ProgramTo;
import org.springframework.core.convert.converter.Converter;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:00 PM
 */
@Component
public class ProgramConverter implements Converter<ProgramModel,ProgramTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ProgramTo convert(ProgramModel source){
		ProgramTo target = new  ProgramTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}