package org.ebs.services.converter;

import org.ebs.model.ProgramModel;
import org.ebs.services.to.Input.ProgramInput;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:00 PM
 */
@Component
public class ProgramConverterInput implements Converter<ProgramInput,ProgramModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ProgramModel convert(ProgramInput source){
		ProgramModel target = new  ProgramModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}