package org.ebs.services.converter;

import org.ebs.model.ProjectModel;
import org.ebs.services.to.ProjectTo;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:02 PM
 */
@Component
public class ProjectConverter implements Converter<ProjectModel,ProjectTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ProjectTo convert(ProjectModel source){
		ProjectTo target = new  ProjectTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}