package org.ebs.services.converter;

import org.ebs.model.ProjectModel;
import org.ebs.services.to.Input.ProjectInput;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:02 PM
 */
@Component
public class ProjectConverterInput implements Converter<ProjectInput,ProjectModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ProjectModel convert(ProjectInput source){
		ProjectModel target = new  ProjectModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}