package org.ebs.services.converter;

import org.ebs.model.PurposeModel;
import org.ebs.services.to.PurposeTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 10-Sep-2020 12:19:53 AM
 */
@Component
  class PurposeConverter implements Converter<PurposeModel,PurposeTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public PurposeTo convert(PurposeModel source){
		PurposeTo target = new  PurposeTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}