package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.ReportModel;
import org.ebs.services.to.ReportTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:20:00 AM
 */
@Component
  class ReportConverter implements Converter<ReportModel,ReportTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ReportTo convert(ReportModel source){
		ReportTo target = new  ReportTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}