package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.ReportModel;
import org.ebs.services.to.Input.ReportInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:20:00 AM
 */
@Component
  class ReportConverterInput implements Converter<ReportInput,ReportModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ReportModel convert(ReportInput source){
		ReportModel target = new  ReportModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}