package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.RequestCFValueModel;
import org.ebs.services.to.RequestCFValueTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:14:11 PM
 */
@Component
  class RequestCFValueConverter implements Converter<RequestCFValueModel,RequestCFValueTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public RequestCFValueTo convert(RequestCFValueModel source){
		RequestCFValueTo target = new  RequestCFValueTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}