package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.RequestCFValueModel;
import org.ebs.services.to.Input.RequestCFValueInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:14:11 PM
 */
@Component
  class RequestCFValueConverterInput implements Converter<RequestCFValueInput,RequestCFValueModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public RequestCFValueModel convert(RequestCFValueInput source){
		RequestCFValueModel target = new  RequestCFValueModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}