package org.ebs.services.converter;

import org.ebs.model.RoleModel;
import org.ebs.services.to.Input.RoleInput;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:08 PM
 */
@Component
public class RoleConverterInput implements Converter<RoleInput,RoleModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public RoleModel convert(RoleInput source){
		RoleModel target = new  RoleModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}