package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.SchemaModel;
import org.ebs.services.to.Input.SchemaInput;
import org.ebs.services.to.SchemaTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:10 PM
 */
@Component
public class SchemaConverterInput implements Converter<SchemaInput,SchemaModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public SchemaModel convert(SchemaInput source){
		SchemaModel target = new  SchemaModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}