package org.ebs.services.converter;

import org.springframework.beans.BeanUtils;
import org.ebs.model.SchemeModel;
import org.ebs.services.to.SchemeTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:14:20 PM
 */
@Component
  class SchemeConverter implements Converter<SchemeModel,SchemeTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public SchemeTo convert(SchemeModel source){
		SchemeTo target = new  SchemeTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}