package org.ebs.services.converter;

import org.ebs.model.SchemeModel;
import org.ebs.services.to.Input.SchemeInput;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:11 PM
 */
@Component
public class SchemeConverterInput implements Converter<SchemeInput,SchemeModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public SchemeModel convert(SchemeInput source){
		SchemeModel target = new  SchemeModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}