package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.SeasonModel;
import org.ebs.services.to.SeasonTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 04-Sep-2020 10:05:57 AM
 */
@Component
  class SeasonConverter implements Converter<SeasonModel,SeasonTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public SeasonTo convert(SeasonModel source){
		SeasonTo target = new  SeasonTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}