package org.ebs.services.converter;

import org.springframework.beans.BeanUtils;
import org.ebs.model.SegmentModel;
import org.ebs.services.to.SegmentTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 20-Aug-2020 1:06:26 PM
 */
@Component
  class SegmentConverter implements Converter<SegmentModel,SegmentTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public SegmentTo convert(SegmentModel source){
		SegmentTo target = new  SegmentTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}