package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.ServiceProviderModel;
import org.ebs.services.to.ServiceProviderTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 10-Sep-2020 10:44:33 PM
 */
@Component
  class ServiceProviderConverter implements Converter<ServiceProviderModel,ServiceProviderTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ServiceProviderTo convert(ServiceProviderModel source){
		ServiceProviderTo target = new  ServiceProviderTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}