package org.ebs.services.converter;

import org.ebs.services.to.StatusTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.StatusModel;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:55 AM
 */
@Component
  class StatusConverter implements Converter<StatusModel,StatusTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public StatusTo convert(StatusModel source){
		StatusTo target = new  StatusTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}