package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.StatusModel;
import org.ebs.services.to.Input.StatusInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:55 AM
 */
@Component
  class StatusConverterInput implements Converter<StatusInput,StatusModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public StatusModel convert(StatusInput source){
		StatusModel target = new  StatusModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}