package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.StatusTypeModel;
import org.ebs.services.to.StatusTypeTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:56 AM
 */
@Component
  class StatusTypeConverter implements Converter<StatusTypeModel,StatusTypeTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public StatusTypeTo convert(StatusTypeModel source){
		StatusTypeTo target = new  StatusTypeTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}