package org.ebs.services.converter;

import org.springframework.beans.BeanUtils;
import org.ebs.model.ThemeStyleModel;
import org.ebs.services.to.Input.ThemeStyleInput;
import org.ebs.services.to.ThemeStyleTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Apr-2020 9:47:54 AM
 */
@Component
public class ThemeStyleConverter implements Converter<ThemeStyleModel,ThemeStyleTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ThemeStyleTo convert(ThemeStyleModel source){
		ThemeStyleTo target = new  ThemeStyleTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}