package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.TraitModel;
import org.ebs.services.to.TraitTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Jul-2020 8:43:42 AM
 */
@Component
  class TraitConverter implements Converter<TraitModel,TraitTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public TraitTo convert(TraitModel source){
		TraitTo target = new  TraitTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}