package org.ebs.services.converter;

import org.ebs.model.TraitModel;
import org.ebs.services.to.Input.TraitInput;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:27 PM
 */
@Component
public class TraitConverterInput implements Converter<TraitInput,TraitModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public TraitModel convert(TraitInput source){
		TraitModel target = new  TraitModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}