package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.TranslationModel;
import org.ebs.services.to.Input.TranslationInput;
import org.ebs.services.to.TranslationTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Apr-2020 9:47:55 AM
 */
@Component
public class TranslationConverter implements Converter<TranslationModel,TranslationTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public TranslationTo convert(TranslationModel source){
		TranslationTo target = new  TranslationTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}