package org.ebs.services.converter;

import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.TypeModel;
import org.ebs.services.to.TypeTo;
import org.springframework.core.convert.converter.Converter;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:20:04 AM
 */
@Component
  class TypeConverter implements Converter<TypeModel,TypeTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public TypeTo convert(TypeModel source){
		TypeTo target = new  TypeTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}