package org.ebs.services.converter;

import org.ebs.model.UserModel;
import org.ebs.services.to.Input.UserInput;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:31 PM
 */
@Component
public class UserConverterInput implements Converter<UserInput,UserModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public UserModel convert(UserInput source){
		UserModel target = new  UserModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}