package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.VendorModel;
import org.ebs.services.to.Input.VendorInput;
import org.ebs.services.to.VendorTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:33 PM
 */
@Component
public class VendorConverter implements Converter<VendorModel,VendorTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public VendorTo convert(VendorModel source){
		VendorTo target = new  VendorTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}